import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { CiLocationArrow1 } from "react-icons/ci";
import { BsEmojiSmile } from "react-icons/bs";
import getUserInfo from "../requests/getUserInfo.js";
import { io } from "socket.io-client";

const Chat = () => {
    const { id } = useParams();
    const [messages, setMessages] = useState([]);
    const [socket, setSocket] = useState(null);
    const [currentUserId, setCurrentUserId] = useState(0);
    const [currentChatId, setCurrentChatId] = useState(0);

    const API_URL = "http://127.0.0.1:8000";

    useEffect(() => {
        const respChat = async () => {
            const resp = await fetch(`${API_URL}/api/users/chats`);
            const data = await resp.json();

            for (const item of data) {
                if (item.chat_name === id) {
                    setCurrentChatId(item.id_chat);
                }
            }
        };

        respChat();
    }, [id]);

    useEffect(() => {
        const respMessages = async () => {
            const resp = await fetch(`${API_URL}/api/messages`);
            const data = await resp.json();

            const chatMessages = data.filter((item) => item.chat_id === currentChatId);
            setMessages(chatMessages);

            const currentUserInfo = await getUserInfo();
            setCurrentUserId(currentUserInfo.id_user);
        };

        respMessages();
    }, [currentChatId]);

    useEffect(() => {
        const newSocket = io(API_URL, { query: { chat_id: currentChatId } });
        setSocket(newSocket);

        newSocket.emit("join chat", { chat_id: currentChatId });

        return () => {
            newSocket.disconnect();
        };
    }, [currentChatId]);

    useEffect(() => {
        if (socket) {
            socket.on("chat message", (newMessage) => {
                setMessages((prevMessages) => [...prevMessages, newMessage]);
            });
        }

        return () => {
            if (socket) {
                socket.off("chat message");
            }
        };
    }, [socket]);

    const sendMessage = (message) => {
        socket.emit("chat message", {
            chat_id: currentChatId,
            user_id: currentUserId,
            message: message,
        });
    };

    return (
        <div>
            <div>
                <span>Open chat: {id}</span>
            </div>
            <div>
                {messages.map((item, idx) => (
                    <div key={idx}>
                        <span className="text-slate-700">{item.message}</span>
                    </div>
                ))}
            </div>
            <div className="flex gap-3">
                <input
                    type="text"
                    placeholder="Type a message here"
                    className="border-solid border-b border-slate-400 outline-none"
                    onKeyDown={(ev) => {
                        if (ev.key === "Enter") {
                            if (ev.target.value.trim() === "") {
                                return;
                            } else {
                                sendMessage(ev.target.value);
                                ev.target.value = "";
                            }
                        }
                    }}
                />
                <CiLocationArrow1
                    className="w-8 h-8 px-2 bg-[#2A8BF2] rounded-full cursor-pointer"
                    onClick={(ev) => {
                        if (ev.target.value.trim() === "") {
                            return;
                        } else {
                            sendMessage(ev.target.value);
                            ev.target.value = "";
                        }
                    }}
                />
                <BsEmojiSmile className="w-8 h-8 px-2 rounded-full cursor-pointer" />
            </div>
        </div>
    );
};

export default Chat;
