import getUserInfo from "../requests/getUserInfo.js";

const ChatSkeleton = () => {
    const [chats, setChats] = useState([]);
    const [userId, setUserId] = useState(null);

    const API_URL = "http://127.0.0.1:8000/api/users/chats";

    useEffect(() => {
        const fetchData = async () => {
            const respChats = await fetch(API_URL).then(resp => resp.json()).then(data => {
                for (const item of data) {
                    setChats(Array(item));
                }
            });

            const respUserInfo = await getUserInfo();

            setUserId(respUserInfo.id_user);
            console.log(chats);

            return { respChats, respUserInfo };
        }

        fetchData();
    }, []);

