import { useEffect, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import getUsers from "../requests/getUsers.js";
import getUserInfo from "../requests/getUserInfo.js";
import ChatSkeleton from "./ChatSkeleton";
import Chat from "./Chat";

const CreateChat = () => {
    const [createChat, setCreateChat] = useState(false);
    const [users, setUsers] = useState([]);
    const [chats, setChats] = useState([]);

    const [successMessage, setSuccessMessage] = useState('');
    const [successStatus, setSuccessStatus] = useState(false);

    const [failedMessage, setFailedMessage] = useState('');
    const [failedStatus, setFailedStatus] = useState(false);

    const [currentUserId, setCurrentUserId] = useState(0);
    const [targetUserId, setTargetUserId] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            const users = await getUsers();
            const currentUser = await getUserInfo();

            setCurrentUserId(currentUser.id_user);

            const filteredUsers = users.filter(item => {
                if (item.id_user !== currentUser.id_user) {
                    setUsers(Array(item));
                };
            });

            return filteredUsers;
        };

        fetchData();
    }, []);

    const onCreateChat = async (currentUserId, targetUserId) => {
        const API_URL = "http://127.0.0.1:8000/api/create_chat";
        const generateUuid = uuidv4();

        const resp = await fetch(API_URL, {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                chat_name: generateUuid,
                current_id: currentUserId,
                target_id: targetUserId
            })
        });

        const data = await resp.json();

        if (resp.status === 200) {
            setSuccessStatus(!successStatus);
            setSuccessMessage(data.success);
        } else {
            setFailedStatus(!failedStatus);
            setFailedMessage(data.error);
        }

        return data;
    };

    return (
        <div>
            <div>
                <button className="bg-zinc-300 hover:bg-zinc-500 rounded-md px-2 py-2" onClick={() => setCreateChat(!createChat)}>Create new chat</button>
            </div>
            {createChat ? (
                <div className="flex flex-col self-start gap-3 items-center">
                    {users.map((item, index) => (
                        <div key={index} className="flex flex-col gap-1 px-2 py-2 border-solid border-2 border-slate-400">
                            <span>{item.name} {item.surname}</span>
                            <button className="px-1 py-1 bg-zinc-300 rounded-md hover:bg-zinc-500" id={item.id_user} onClick={(ev) => {
                                onCreateChat(currentUserId, parseInt(ev.target.id));
                            }}>Create chat</button>
                        </div>
                    ))}
                </div>
            ) : null}
            {successStatus ? (
                <span>{successMessage}</span>
            ) : null}
            {failedStatus ? (
                <span>{failedMessage}</span>
            ) : null}
            {<ChatSkeleton />}
        </div >
    )
};

export default CreateChat;
