import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CiLocationArrow1 } from "react-icons/ci";
import { BsEmojiSmile } from "react-icons/bs";
import getUserInfo from "../requests/getUserInfo.js";

const Chat = () => {
    const { id } = useParams();
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [socket, setSocket] = useState(null);
    const [currentUserId, setCurrentUserId] = useState(0);

    const API_URL = 'http://127.0.0.1:8000/api';
    const WS_URL = 'ws://127.0.0.1:3000';

    useEffect(() => {
        const fetchData = async () => {
            const respMessages = await fetch(`${API_URL}/messages`).then(resp => resp.json());
            setMessages(respMessages);

            const currentUserInfo = await getUserInfo();
            setCurrentUserId(currentUserInfo.id_user);
        };

        fetchData();

        setTimeout(() => {
            const newSocket = new WebSocket(`${WS_URL}/${id}`);
            setSocket(newSocket);

            console.log(newSocket.readyState);

            if (socket === null) {
                return console.error("АШИБКА");
            } else {
                newSocket.addEventListener('open', () => {
                    console.log('WebSocket connection is open.');

                    const user_id = currentUserId;
                    const initialMessage = { type: 'initial', user_id };
                    newSocket.send(JSON.stringify(initialMessage));
                });

                newSocket.addEventListener('message', (event) => {
                    console.log(newSocket);
                    const data = JSON.parse(event.data);

                    if (data.type === 'message') {
                        setMessages((prevMessages) => [...prevMessages, data.content]);
                    }
                });
            }
        }, 1000)
    }, []);

    const sendMessage = () => {
        console.log(`socket: ${socket.readyState} websocket: ${WebSocket.OPEN}`);
        if (inputValue.trim() !== '') {
            if (socket.readyState === WebSocket.OPEN) {
                const user_id = currentUserId;
                const messageData = { type: 'message', user_id, content: inputValue };

                console.log(socket);
                socket.send(JSON.stringify(messageData));

                setInputValue('');
            } else {
                console.log('ws connection not open');
            }
        }
    };

    return (
        <div>
            <div>
                <span>Open chat: {id}</span>
            </div>
            <div>
                {messages.map((item, idx) => (
                    <div key={idx}>
                        <span>{item}</span>
                    </div>
                ))}
            </div>
            <div className="flex gap-3">
                <input
                    type="text"
                    placeholder="Type a message here"
                    className="border-solid border-b border-slate-400 outline-none"
                    value={inputValue}
                    onChange={(ev) => setInputValue(ev.target.value)}
                />
                <CiLocationArrow1
                    className="w-8 h-8 px-2 bg-[#2A8BF2] rounded-full cursor-pointer"
                    onClick={sendMessage}
                />
                <BsEmojiSmile className="w-8 h-8 px-2 rounded-full cursor-pointer" />
            </div>
        </div>
    );
};

export default Chat;
