import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import getUserInfo from "../requests/getUserInfo.js";

const ChatSkeleton = () => {
    const [chats, setChats] = useState([]);
    const [userId, setUserId] = useState(null);

    const history = useHistory();

    const API_URL = "http://127.0.0.1:8000/api/users/chats";

    useEffect(() => {
        const fetchData = async () => {
            const respChats = await fetch(API_URL).then(resp => resp.json()).then(data => {
                for (const item of data) {
                    setChats(Array(item));
                }
            });

            const respUserInfo = await getUserInfo();

            setUserId(respUserInfo.id_user);

            return { respChats, respUserInfo };
        }

        fetchData();
    }, []);

    const openChat = (id) => {
        return history.push(`/chat?id=${id}`);
    };

    const convertDate = (date) => {
        const [newDate, splitDate] = String(date).split("T");

        return newDate;
    };

    return (
        <div>
            {chats.map((item, idx) => (
                <div key={idx} className="cursor-pointer" onClick={() => openChat(item.chat_name)}>
                    {userId === item.current_user_id ? (
                        <div className="px-12 py-6 border-solid border-2 border-slate-300 flex flex-col items-center justify-center gap-2">
                            <span>{item.chat_name}</span>
                            <span>{convertDate(item.created_date)}</span>
                        </div>
                    ) : null}
                </div>
            ))}
        </div>
    )
};

export default ChatSkeleton;
