import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CiLocationArrow1 } from "react-icons/ci";
import { BsEmojiSmile } from "react-icons/bs";

const Chat = () => {
    const { id } = useParams();
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [socket, setSocket] = useState(null);

    const API_URL = 'http://127.0.0.1:8000/api';
    const WS_URL = 'ws://127.0.0.1:3000'; // Обновите на ваш реальный URL WebSocket сервера

    useEffect(() => {
        // Инициализация WebSocket соединения
        const newSocket = new WebSocket(`${WS_URL}/${id}`);
        setSocket(newSocket);

        // Отправка запроса на получение предыдущих сообщений
        const fetchData = async () => {
            const respMessages = await fetch(`${API_URL}/messages`).then(resp => resp.json());
            setMessages(respMessages);
        };

        fetchData();

        // Обработка входящих сообщений
        newSocket.addEventListener('message', (event) => {
            const data = JSON.parse(event.data);

            if (data.type === 'message') {
                // Выводим новое сообщение
                setMessages((prevMessages) => [...prevMessages, data.content]);
            }
        });

        // Закрываем WebSocket соединение при размонтировании компонента
        return () => {
            if (socket) {
                socket.close();
            }
        };
    }, [id, socket]);

    const sendMessage = () => {
        if (inputValue.trim() !== '') {
            // Отправляем сообщение на сервер через WebSocket
            const user_id = 1; // Замените на реальный user_id
            const messageData = { type: 'message', user_id, content: inputValue };

            if (socket) {
                socket.send(JSON.stringify(messageData));
            }

            // Очищаем поле ввода
            setInputValue('');
        }
    };

    return (
        <div>
            <div>
                <span>Open chat: {id}</span>
            </div>
            <div>
                {messages.map((item, idx) => (
                    <div key={idx}>
                        <span>{item}</span>
                    </div>
                ))}
            </div>
            <div className="flex gap-3">
                <input
                    type="text"
                    placeholder="Type a message here"
                    className="border-solid border-b border-slate-400 outline-none"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                />
                <CiLocationArrow1
                    className="w-8 h-8 px-2 bg-[#2A8BF2] rounded-full cursor-pointer"
                    onClick={sendMessage}
                />
                <BsEmojiSmile className="w-8 h-8 px-2 rounded-full cursor-pointer" />
            </div>
        </div>
    );
};

export default Chat;
