import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CiLocationArrow1 } from "react-icons/ci";
import { BsEmojiSmile } from "react-icons/bs";
import getUserInfo from "../requests/getUserInfo.js";
import { io } from "socket.io-client";

const Chat = () => {
    const { id } = useParams();
    const [messages, setMessages] = useState([]);
    const [socket, setSocket] = useState(null);
    const [currentUserId, setCurrentUserId] = useState(0);
    const [currentChatId, setCurrentChatId] = useState(0);

    const API_URL = "http://127.0.0.1:8000";

    useEffect(() => {
        const fetchData = async () => {
            const respChat = await fetch(`${API_URL}/api/users/chats`).then(resp => resp.json()).then(data => {
                for (const item of data) {
                    console.log(item.chat_name)
                    if (item.chat_name === id) {
                        setCurrentChatId(item.id_chat);
                    }
                }
            })

            setTimeout(async () => {
                await fetch(`${API_URL}/api/messages`).then(resp => resp.json()).then(data => {
                    for (const item of data) {
                        console.log(currentChatId)
                        if (item.chat_id === currentChatId) {
                            console.log(item);
                        }
                    }
                });
            }, 1000);

            const currentUserInfo = await getUserInfo();

            setCurrentUserId(currentUserInfo.id_user);

            return { respChat };
        };

        fetchData();

        const newSocket = io(API_URL, { query: { chat_name: currentChatId } });
        setSocket(newSocket);

        return () => {
            newSocket.disconnect();
        };
    }, []);

    const sendMessage = (message) => {
        socket.emit("chat message", {
            chat_id: currentChatId,
            user_id: currentUserId,
            message: message
        })
    };

    return (
        <div>
            <div>
                <span>Open chat: {id}</span>
            </div>
            <div>
                {messages.map((item, idx) => (
                    <div key={idx}>
                        <span className="text-slate-700">{item.message}</span>
                    </div>
                ))}
            </div>
            <div className="flex gap-3">
                <input type="text" placeholder="Type a message here" className="border-solid border-b border-slate-400 outline-none"
                    onKeyDown={(ev) => {
                        if (ev.key === "Enter") {
                            sendMessage(ev.target.value);
                            ev.target.value = "";
                        }
                    }} />
                <CiLocationArrow1 className="w-8 h-8 px-2 bg-[#2A8BF2] rounded-full cursor-pointer" onClick={(ev) => { sendMessage(ev.target.value); ev.target.value = "" }} />
                <BsEmojiSmile className="w-8 h-8 px-2 rounded-full cursor-pointer" />
            </div>
        </div>
    )
};

export default Chat;
